package core;import org.springframework.context.ApplicationContext;import org.springframework.context.support.ClassPathXmlApplicationContext;/** * Created with IntelliJ IDEA. * User: jicui * Date: 12/22/13 * Time: 7:07 PM * To change this template use File | Settings | File Templates. */public class Application {    public static void main(String[] args) throws PerformanceException {        ApplicationContext ctx = new ClassPathXmlApplicationContext("application.xml");        Performer duke = (Performer) ctx.getBean("duke");        duke.perform();        Performer poeticDuke = (Performer) ctx.getBean("poeticDuke");        poeticDuke.perform();    }}