package core;import sun.management.snmp.jvmmib.JvmMemPoolEntryMBean;/** * Created with IntelliJ IDEA. * User: jicui * Date: 12/22/13 * Time: 7:19 PM * To change this template use File | Settings | File Templates. */public class PoeticJuggler extends Juggler{    private Poem poem;    public PoeticJuggler(Poem poem) {        this.poem = poem;    }    public PoeticJuggler(int beanBags, Poem poem) {        super(beanBags);        this.poem = poem;    }    @Override    public void perform() throws PerformanceException {        super.perform();        System.out.println("while reciting..");        poem.recite();    }}