package core.annotation;import core.*;import org.springframework.context.ApplicationContext;import org.springframework.context.annotation.AnnotationConfigApplicationContext;import org.springframework.context.annotation.Bean;import org.springframework.context.annotation.ComponentScan;import org.springframework.context.annotation.Configuration;/** * Created with IntelliJ IDEA. * User: jicui * Date: 12/29/13 * Time: 11:13 AM * To change this template use File | Settings | File Templates. */@Configurationpublic class AppConfig {    /**     * create a bean with type of Poem and name of sonnect29     * construct of the bean by the method body method     * @return     */    @Bean    public Poem sonnet29() {        return new Sonnet29();    }    /**     * construct the bean with other bean defination     * @return     */    @Bean    public Performer poeticDuke() {        return new PoeticJuggler(sonnet29());    }    public static void main(String[] args) throws PerformanceException {        ApplicationContext context =                new AnnotationConfigApplicationContext(AppConfig.class);        Performer poeticDuke1 =(Performer)context.getBean("poeticDuke");        poeticDuke1.perform();    }}