package core.aop;import core.Auditorium;import core.PerformanceException;import core.Performer;import org.springframework.context.ApplicationContext;import org.springframework.context.support.ClassPathXmlApplicationContext;/** * Created with IntelliJ IDEA. * User: jicui * Date: 12/22/13 * Time: 7:07 PM * To change this template use File | Settings | File Templates. */public class Application {    public static void main(String[] args) throws PerformanceException {        ApplicationContext ctx = new ClassPathXmlApplicationContext("aop/aop.xml");        Performer duke = (Performer) ctx.getBean("duke");        duke.perform();        ((Contestant)duke).receiveAward();       Thinker thinker=(Thinker)ctx.getBean("thinker");       thinker.thinkOfSomething("have fun");    }}