package core.aop;import org.aspectj.lang.ProceedingJoinPoint;/** * Created with IntelliJ IDEA. * User: jicui * Date: 12/29/13 * Time: 1:21 PM * To change this template use File | Settings | File Templates. */public class Audience {    public void takeSeats(){      System.out.println("The audience is taking their seats");    }    public void turnOffCellPhones(){        System.out.println("the audience is turnning off their cell phones");    }    public void applaud(){        System.out.println("the audience is applauding for the best performance");    }    public void demandRefund(){        System.out.println("Woo we want our money back");    }    /**     * define the around advise,so that we can get the context runtime env,and doing the advice     *     * @param proceedingJoinPoint     */    public void watchPerformance(ProceedingJoinPoint proceedingJoinPoint){        long start=System.currentTimeMillis();        try {            proceedingJoinPoint.proceed();        } catch (Throwable throwable) {            throwable.printStackTrace();        }        long end=System.currentTimeMillis();        System.out.println("Time elapsed in millseconds"+(end-start));    }}