package dbaccess;/** * Created with IntelliJ IDEA. * User: jicui * Date: 1/3/14 * Time: 10:07 PM * To change this template use File | Settings | File Templates. */public class Spitter {    private String id;    private String userName;    private String password;    private String fullName;    private String email;    public String getId() {        return id;    }    public void setId(String id) {        this.id = id;    }    public String getUserName() {        return userName;    }    public void setUserName(String userName) {        this.userName = userName;    }    public String getPassword() {        return password;    }    public void setPassword(String password) {        this.password = password;    }    public String getFullName() {        return fullName;    }    public void setFullName(String fullName) {        this.fullName = fullName;    }    public String getEmail() {        return email;    }    public void setEmail(String email) {        this.email = email;    }}