package dbaccess;import javax.persistence.Column;import javax.persistence.Entity;import javax.persistence.Id;import javax.persistence.Table;/** * Created with IntelliJ IDEA. * User: jicui * Date: 1/3/14 * Time: 10:07 PM * To change this template use File | Settings | File Templates. */@Entity(name = "dbaccess.Spitter")@Table(name = "SPITTER")public class Spitter {    @Id    @Column(name = "ID")    private Long id;    @Column(name = "USER_NAME")    private String userName;    @Column(name = "PASSWORD")    private String password;    @Column(name = "FULL_NAME")    private String fullName;    @Column(name = "EMAIL")    private String email;    public Long getId() {        return id;    }    public void setId(Long id) {        this.id = id;    }    public String getUserName() {        return userName;    }    public void setUserName(String userName) {        this.userName = userName;    }    public String getPassword() {        return password;    }    public void setPassword(String password) {        this.password = password;    }    public String getFullName() {        return fullName;    }    public void setFullName(String fullName) {        this.fullName = fullName;    }    public String getEmail() {        return email;    }    public void setEmail(String email) {        this.email = email;    }}