import org.testng.Assert;import org.testng.annotations.Test;import com.github.dreamhead.moco.HttpServer;import org.apache.http.client.fluent.Content;import org.apache.http.client.fluent.Request;import com.github.dreamhead.moco.Runnable;import java.io.IOException;import static com.github.dreamhead.moco.Moco.*;import static com.github.dreamhead.moco.Runner.*;import static org.hamcrest.core.Is.is;/** * Created with IntelliJ IDEA. * User: jicui * Date: 3/20/14 * Time: 7:51 PM * To change this template use File | Settings | File Templates. */@Testpublic class MocoTest {    @Test    public void should_response_as_expected() throws Exception {        HttpServer server = httpserver(12306);        server.response("foo");        running(server, new Runnable() {            @Override            public void run() throws IOException {                Content content = Request.Get("http://localhost:12306").execute().returnContent();                Assert.assertEquals(content.asString(), "foo");            }        });    }}